import React, {useState} from 'react';
import './App.css';
import {Todolist} from "./todolist/Todolist";
import {v1} from "uuid";

export type TasksType = {
    id: string
    title: string
    isDone: boolean
}

const tasks1: TasksType[] = [
    {id: v1(), title: "HTML&CSS", isDone: true},
    {id: v1(), title: "JS", isDone: true},
    {id: v1(), title: "GraphQL", isDone: false},
    {id: v1(), title: "ReactJS", isDone: false},
    {id: v1(), title: "Rest API", isDone: false}
]

export type TodolistsType = {
    id: string
    title: string
    filter: FilterValueType
}

export type FilterValueType = "all" | "active" | "completed"

function App() {
    let [todolists, setTodolists] = useState<Array<TodolistsType>>(
        [
            {id: v1(), title: 'What to learn', filter: 'all'},
            {id: v1(), title: 'What to buy', filter: 'all'},
        ]
    )


    const [tasks, setTasks] = useState<TasksType[]>(tasks1)
    const [filter, setFilter] = useState<FilterValueType>('all')

    function removeTask(id: string) {

        setTasks(tasks.filter(t => t.id !== id))

    }


    function changeFilter(filterValue: FilterValueType, todoId:string) {
        setTodolists(todolists.map(t=> t.id === todoId ? {...t, filter:filterValue}: t))    }

    function addTask(title: string) {
        const newTask = {id: v1(), title, isDone: false}

        setTasks([newTask, ...tasks])
    }

    function changeTaskStatus(id: string, isDone: boolean) {
        setTasks(tasks.map(t => t.id === id ? {...t, isDone: isDone} : t)
        )
    }

    return (
        <div className="App">


            {
                todolists.map(todo => {

                    let filteredTasks = tasks
                    if (todo.filter === 'active') {
                        filteredTasks = tasks.filter(task => task.isDone)
                    }
                    if (todo.filter === 'completed') {
                        filteredTasks = tasks.filter(t => !t.isDone)
                    }

                    return (
                        <Todolist key={todo.id} todoId={todo.id} title={todo.title} tasks={filteredTasks}
                                  removeTask={removeTask} changeFilter={changeFilter}
                                  addTask={addTask} changeTaskStatus={changeTaskStatus} filter={filter}/>
                    )

                })

            }


        </div>
    );
}

export default App;
