import React, {ChangeEvent, FC, useState} from "react";
import {FilterValueType, TasksType} from "../App";

type TodolistPropsType = {
    title: string
    tasks: TasksType[]
    removeTask: (id: string) => void
    changeFilter: (value: FilterValueType) => void
    addTask: (title: string) => void
}

export const Todolist: FC<TodolistPropsType> = ({title, tasks, removeTask, changeFilter, addTask}) => {
    const [value, setValue] = useState('')

    function onChangeInputHandler(e: ChangeEvent<HTMLInputElement>) {
        setValue(e.currentTarget.value)
    }

    function onAddTaskHandler() {
        addTask(value)
        setValue('')
    }

    function onKeyPressHandler(e: React.KeyboardEvent<HTMLInputElement>) {
        if (e.key === 'Enter') {
            addTask(value)
            setValue('')
        }
    }

    function onAllClickHandler() {
        changeFilter('all')
    }

    function onActiveClickHandler() {
        changeFilter('active')
    }

    function onCompletedClickHandler() {
        changeFilter('completed')
    }

    return (
        <div>
            <h3>{title}</h3>
            <div>
                <input value={value} onChange={onChangeInputHandler} onKeyPress={onKeyPressHandler}/>
                <button onClick={onAddTaskHandler}>+</button>
            </div>
            <ul>

                {tasks.map((task) => {
                    function removeTaskHandler() {
                        removeTask(task.id)
                    }

                    return (

                        <div key={task.id}>
                            <li><input type="checkbox" checked={task.isDone}/> <span>{task.title}</span>

                                <button onClick={removeTaskHandler}>✖️</button>
                            </li>

                        </div>
                    )
                })}
            </ul>
            <div>
                <button onClick={onAllClickHandler}>All</button>
                <button onClick={onActiveClickHandler}>Active</button>
                <button onClick={onCompletedClickHandler}>Completed</button>
            </div>
        </div>
    )
}
