import React, {FC} from "react";
import {TasksType} from "../App";

type TodolistPropsType = {
    title: string
    tasks: TasksType[]
}

export const Todolist: FC<TodolistPropsType> = ({title, tasks}) => {


    return (
        <div>
            <h3>{title}</h3>
            <div>
                <input/>
                <button>+</button>
            </div>
            <ul>

                {tasks.map((task) => (
                    <div key={task.id}>
                        <li><input type="checkbox" checked={task.isDone}/> <span>{task.title}</span></li>

                    </div>
                ))}
            </ul>
            <div>
                <button>All</button>
                <button>Active</button>
                <button>Completed</button>
            </div>
        </div>
    )
}
