import React, {FC} from "react";
import {FilterValueType, TasksType} from "../App";

type TodolistPropsType = {
    title: string
    tasks: TasksType[]
    removeTask: (id: string) => void
    changeFilter: (value: FilterValueType) => void
    addTask: (title:string) => void
}

export const Todolist: FC<TodolistPropsType> = ({title, tasks, removeTask, changeFilter, addTask}) => {


    function onAddTaskHandler(){
        addTask()
    }
    return (
        <div>
            <h3>{title}</h3>
            <div>
                <input/>
                <button >+</button>
            </div>
            <ul>

                {tasks.map((task) => {
                    return (

                        <div key={task.id}>
                            <li><input type="checkbox" checked={task.isDone}/> <span>{task.title}</span>

                                <button onClick={() => removeTask(task.id)}>✖️</button>
                            </li>

                        </div>
                    )
                })}
            </ul>
            <div>
                <button onClick={() => changeFilter('all')}>All</button>
                <button onClick={() => changeFilter('active')}>Active</button>
                <button onClick={() => changeFilter('completed')}>Completed</button>
            </div>
        </div>
    )
}
